parameters.jsCode:
let movies = $input.all().map(item => item.json);

let html = `
<style>
  body {
    font-family: 'Georgia', 'Times New Roman', serif;
    margin: 0;
    padding: 0;
    background: #0c0c1a;
  }
  .movie-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 25px;
    padding: 25px;
    justify-items: center;
  }
  .movie-card {
    background: linear-gradient(135deg, #1a1a2e, #16213e);
    border-radius: 15px;
    box-shadow: 0 6px 15px rgba(74, 74, 138, 0.3);
    border: 1px solid #4a4a8a;
    overflow: hidden;
    width: 100%;
    max-width: 300px;
    transition: transform 0.3s, box-shadow 0.3s;
    cursor: pointer;
    position: relative;
  }
  .movie-card:hover {
    transform: translateY(-7px);
    box-shadow: 0 12px 25px rgba(212, 175, 55, 0.3);
    border-color: #d4af37;
  }
  .movie-card img {
    width: 100%;
    height: 400px;
    object-fit: cover;
    display: block;
    border-bottom: 1px solid #4a4a8a;
  }
  .movie-content {
    padding: 20px;
  }
  .movie-title {
    font-size: 1.2rem;
    font-weight: bold;
    margin-bottom: 8px;
    color: #d4af37;
    line-height: 1.3;
  }
  .movie-details {
    font-size: 0.85rem;
    color: #8d8db3;
    margin-bottom: 10px;
    line-height: 1.4;
  }
  .movie-rating {
    font-size: 0.9rem;
    margin-bottom: 12px;
    display: flex;
    align-items: center;
    gap: 10px;
  }
  .rating-number {
    color: #d4af37;
    font-weight: bold;
    font-size: 1rem;
  }
  .stars {
    display: inline-block;
    color: #d4af37;
    font-size: 1rem;
  }
  .movie-plot {
    font-size: 0.85rem;
    color: #b8b8d1;
    line-height: 1.4;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }
  .movie-meta {
    display: flex;
    justify-content: space-between;
    font-size: 0.8rem;
    color: #6b6b9e;
    margin-top: 10px;
  }
  @media (max-width: 600px) {
    .movie-container {
      grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
      padding: 15px;
      gap: 20px;
    }
    .movie-card {
      max-width: 100%;
    }
  }
</style>

<div class="movie-container">
  ${movies.map(movie => {
    // Convert IMDb rating to stars (0-10 scale)
    const imdbRating = parseFloat(movie.imdbRating);
    const starCount = Math.round(imdbRating);
    let stars = 'â˜…'.repeat(starCount) + 'â˜†'.repeat(10 - starCount);

    // Get Rotten Tomatoes rating if available
    const rtRating = movie.Ratings.find(r => r.Source === 'Rotten Tomatoes');
    const rtValue = rtRating ? rtRating.Value : 'N/A';

    return `
      <div class="movie-card" onclick="alert('Title: ${movie.Title}\\nYear: ${movie.Year}\\nRated: ${movie.Rated}\\nRuntime: ${movie.Runtime}\\nGenre: ${movie.Genre}\\nDirector: ${movie.Director}\\nIMDb Rating: ${movie.imdbRating}\\nRotten Tomatoes: ${rtValue}\\n\\nPlot: ${movie.Plot}')">
        <img src="${movie.Poster !== 'N/A' ? movie.Poster : 'https://via.placeholder.com/300x400/1a1a2e/d4af37?text=No+Image'}" alt="${movie.Title}">
        <div class="movie-content">
          <div class="movie-title">${movie.Title}</div>
          <div class="movie-details">
            ${movie.Year} â€¢ ${movie.Runtime} â€¢ ${movie.Rated}
          </div>
          <div class="movie-rating">
            <span class="rating-number">${movie.imdbRating}/10</span>
            <span class="stars">${stars}</span>
          </div>
          <div class="movie-plot">${movie.Plot}</div>
          <div class="movie-meta">
            <span>${movie.Genre.split(',')[0]}</span>
            <span>${rtValue}</span>
          </div>
        </div>
      </div>
    `;
  }).join('')}
</div>
`;

return [{ json: { moviesHtml: html } }];

// Get the AI output text
const text = $json["output"];

// Extract JSON array from the text
const match = text.match(/\[([\s\S]*?)\]/);

if (match) {
  const movies = JSON.parse(match[0]);
  return movies.map(movie => ({ json: { movieTitle: movie } }));
} else {
  return [];
}
